package week1.day1;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int age = 20;
		long number = 123214561234l;
		//(for more than 9 digits use long data type)
		
		float mileage = 45.1f;
		double bankbalance = 75000.856;
		
		char logo = 'S';
		String brand = "SWIFT";
	
		boolean ispresent = true;
		
		System.out.println("Age is : " +age);
		System.out.println("Phone number is : " +number + ",   Mileage is : " + mileage);
		System.out.println("bank balance is : " + bankbalance  + "\n"   +  "Logo is : " + logo);
		System.out.println("Brand name is : " +brand +"\n" + "Boolean values is : " + ispresent);

	}

}
